<script>	$(document).ready(function()        {							var watermark = 'Autocomplete';									<!------------------- watermark user ------------>			$('#user_nm').val(watermark).addClass('watermark');			//if blur and no value inside, set watermark text and class again.			$('#user_nm').blur(function(){				if ($(this).val().length == 0){					$(this).val(watermark).addClass('watermark');				}			});		 			//if focus and text is watermrk, set it to empty and remove the watermark class			$('#user_nm').focus(function(){				if ($(this).val() == watermark){					$(this).val('').removeClass('watermark');				}		    });			<!------------------- watermark user ------------>		});				$(function() {						<!------------------- autocomplete user ------------>			$( "#user_nm" ).autocomplete({				minLength: 3,				source: "<?=HOME?>monitoring.ba.auto/pic",				focus: function( event, ui ) {					$( "#user_nm" ).val( ui.item.NAME );					return false;				},				select: function( event, ui ) {					$( "#user_nm" ).val( ui.item.NAME );					$( "#user_ba" ).val( ui.item.ID );					return false;				}			})			.data( "autocomplete" )._renderItem = function( ul, item ) {				return $( "<li></li>" )					.data( "item.autocomplete", item )					.append( "<a>" + item.NAME + " | " + item.NIPP + "</a>" )					.appendTo( ul );			};			<!------------------- autocomplete user ------------>					});				</script><?	$id_ba  = $_GET['id_ba'];?><br>		<table>			<tr height='20'>				<td class="form-field-caption" align="right"></td>				<td class="form-field-caption" align="right"></td>				<td></td>						</tr>			<tr>				<td class="form-field-caption" align="right">USER PIC</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="user_nm" id="user_nm" title="Autocomplete" size="15"/>					<input type="hidden" name="user_ba" id="user_ba"/>				</td>			</tr>			<tr>				<td colspan="3">&nbsp;</td>			</tr>			<tr>				<td colspan="3" align="right">								<input type="button" name="Insert BA" value="&nbsp;Assign&nbsp;" onclick="assign_pic('<?=$id_ba?>')"/>				</td>			</tr>			<tr height='20'>				<td class="form-field-caption" align="right"></td>				<td class="form-field-caption" align="right"></td>				<td></td>						</tr>		</table>		