<?php$id_user = $_SESSION["ID_USER"];$ref_humas = $_POST['REF'];$simop = $_POST['SIMOP'];$terminal = $_POST['TERMINAL'];if($simop=='kapal'){	$plg = $_POST['KD_AGEN'];}else{	$plg = $_POST['KD_PLG'];}if(($ref_humas==NULL)||($simop==NULL)||($terminal==NULL)||($plg==NULL)){	echo "NO";}else{	$db=getDb();	$db->query("INSERT INTO BERITA_ACARA 				(NO_REF_HUMAS,ACCOUNT_KEU,SIMOP,TERMINAL) 				VALUES 				('$ref_humas','$plg','$simop','$terminal')");	$db->query("INSERT INTO HIST_BERITA_ACARA 				(NO_REF_HUMAS,ACCOUNT_KEU,STATUS,SIMOP,TERMINAL,USER_UPDATE) 				VALUES 				('$ref_humas','$plg','MASUK','$simop','$terminal','$id_user')");					echo "OK";}?>