<?php	$voyin=$_GET['voyin'];	$voyout=$_GET['voyout'];	$ct=$_GET['id_ct'];	$db=getDb();  $sq="select z.NO_CONTAINER, z.ID_NOTA,z.NO_FAKTUR, z.ID_REQ, z.EMKL, z.STATUS,z.ALAMAT, z.VESSEL, z.VOYAGE_IN, z.VOYAGE_OUT, z.TGL_SIMPAN, z.TGL_PAYMENT, z.PAYMENT_VIA,z.TOTAL,z.COA,z.KD_MODUL,z.KET, y.TANGGAL_RECEIPT, z.TGL_BERLAKU, z.USER_ID, tu.NAME        from         (select NO_CONTAINER, nota_receiving_h.ID_NOTA,NO_FAKTUR, nota_receiving_h.ID_REQ, EMKL, STATUS,ALAMAT, nota_receiving_h.VESSEL, nota_receiving_h.VOYAGE_IN, nota_receiving_h.VOYAGE_OUT, TGL_SIMPAN, TGL_PAYMENT, PAYMENT_VIA,TOTAL,COA,KD_MODUL,'ANNE' KET,        '' AS TGL_BERLAKU, ID_USER AS USER_ID        from nota_receiving_h inner join req_receiving_d on (trim(nota_receiving_h.ID_REQ) = trim(req_receiving_d.NO_REQ_ANNE))        union        select NO_CONTAINER, nota_delivery_h.ID_NOTA,NO_FAKTUR, nota_delivery_h.ID_REQ, EMKL, STATUS,ALAMAT, nota_delivery_h.VESSEL, nota_delivery_h.VOYAGE_IN, nota_delivery_h.VOYAGE_OUT, TGL_NOTA AS TGL_SIMPAN, TGL_PAYMENT, PAYMENT_VIA, TOTAL,COA,KD_MODUL, 'SP2' KET,  TO_CHAR(nota_delivery_h.TGL_SP2,'DD-MON-YYYY') AS TGL_BERLAKU , ID_USER AS USER_ID        from nota_delivery_h inner join req_delivery_d on (trim(nota_delivery_h.ID_REQ) = trim(req_delivery_d.no_req_dev))        union        select NO_CONTAINER, nota_batalmuat_h.NO_NOTA AS ID_NOTA, NO_FAKTUR, nota_batalmuat_h.ID_BATALMUAT AS ID_REQ, EMKL, nota_batalmuat_h.STATUS, ALAMAT, VESSEL , ' ' AS VOYAGE_IN, VOYAGE AS VOYAGE_OUT, TGL_NOTA AS TGL_SIMPAN,TGL_PAYMENT, PAYMENT_VIA, nota_batalmuat_h.TOTAL, COA, nota_batalmuat_h.JENIS AS KD_MODUL,'BM' KET, '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID                from nota_batalmuat_h inner join tb_batalmuat_d on (trim(nota_batalmuat_h.ID_BATALMUAT) = trim(tb_batalmuat_d.ID_BATALMUAT))        union        select NO_CONTAINER, BH_NOTA.ID_NOTA, NO_FAKTUR, BH_NOTA.ID_REQUEST AS ID_REQ, EMKL, STATUS, ALAMAT_EMKL AS ALAMAT, VESSEL , ' ' AS VOYAGE_IN, VOYAGE AS VOYAGE_OUT, TGL_CETAK AS TGL_SIMPAN,TGL_PAYMENT, PAYMENT_VIA, TOTAL, COA, '' AS KD_MODUL, 'BH' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from BH_NOTA INNER JOIN BH_DETAIL_NOTA ON (trim(BH_NOTA.ID_NOTA)=trim(BH_DETAIL_NOTA.ID_NOTA))        union        select NO_CONTAINER, EXMO_NOTA.ID_NOTA, NO_FAKTUR, ID_REQUEST AS ID_REQ, EMKL, STATUS, ALAMAT, '' AS VESSEL , ' ' AS VOYAGE_IN, ' ' AS VOYAGE_OUT, TGL_CETAK_NOTA AS TGL_SIMPAN,TGL_PAYMENT, PAYMENT_VIA, TOTAL, COA, '' AS KD_MODUL, 'EXMO' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from EXMO_NOTA INNER JOIN EXMO_DETAIL_NOTA ON (trim(EXMO_NOTA.ID_NOTA)=trim(EXMO_DETAIL_NOTA.ID_NOTA))        union        select NO_CONTAINER, A.ID_NOTA, A.NO_FAKTUR, A.ID_REQUEST AS ID_REQ, A.CUSTOMER AS EMKL, A.STATUS, A.ALAMAT, B.VESSEL AS VESSEL , ' ' AS VOYAGE_IN, B.VOYAGE AS VOYAGE_OUT, A.TGL_CETAK AS TGL_SIMPAN,A.TGL_PAYMENT, A.PAYMENT_VIA, A.TOTAL, A.COA, '' AS KD_MODUL, 'TRANS' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from NOTA_TRANSHIPMENT_H A LEFT JOIN REQ_TRANSHIPMENT_H B ON B.ID_REQ=A.ID_REQUEST INNER JOIN REQ_TRANSHIPMENT_D C ON (trim(A.ID_REQUEST) = trim(C.ID_REQ))        union        select NO_CONTAINER, NOTA_REEKSPOR_H.ID_NOTA, NO_FAKTUR, NOTA_REEKSPOR_H.ID_REQUEST AS ID_REQ, NM_PEMILIK, STATUS, ALAMAT, NM_KAPAL AS VESSEL , VOYAGE_IN, VOYAGE_OUT, TGL_SIMPAN, TGL_PAYMENT, PAYMENT_VIA, TOTAL, COA, '' AS KD_MODUL, 'REEX' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from NOTA_REEKSPOR_H INNER JOIN REQ_REEKSPOR_D ON (trim(NOTA_REEKSPOR_H.ID_REQUEST) = trim(REQ_REEKSPOR_D.ID_REQUEST))        UNION        select NO_CONTAINER, A.ID_NOTA, A.NO_FAKTUR, A.ID_REQUEST AS ID_REQ, A.CUSTOMER AS EMKL, A.STATUS, A.ALAMAT, B.VESSEL AS VESSEL , ' ' AS VOYAGE_IN, B.VOYAGE AS VOYAGE_OUT, A.TGL_CETAK AS TGL_SIMPAN,A.TGL_PAYMENT, A.PAYMENT_VIA, A.TOTAL, A.COA, '' AS KD_MODUL, 'TRANS' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from NOTA_REEXPORT_H A LEFT JOIN REQ_REEXPORT_H B ON B.ID_REQ=A.ID_REQUEST INNER JOIN REQ_REEXPORT_D C ON (trim(A.ID_REQUEST) = trim(C.ID_REQ))        union        select NO_CONTAINER, NOTA_HICOSCAN_H.ID_NOTA,NOTA_HICOSCAN_H.ID_NOTA AS NO_FAKTUR ,NOTA_HICOSCAN_H.ID_REQUEST AS ID_REQ, EMKL AS NM_PEMILIK, STATUS, ALAMAT_EMKL AS ALAMAT, VESSEL ,VOYAGE AS VOYAGE_IN, NULL AS VOYAGE_OUT, TGL_CETAK AS TGL_SIMPAN, TGL_PAYMENT, PAYMENT_VIA, TOTAL, COA, '' AS KD_MODUL, 'HICO' KET,        '' AS TGL_BERLAKU , USER_ID_PAYMENT AS USER_ID        from NOTA_HICOSCAN_H INNER JOIN NOTA_HICOSCAN_D ON (trim(NOTA_HICOSCAN_H.ID_NOTA) = trim(NOTA_HICOSCAN_D.ID_NOTA))        ) z left join tth_nota_all y on z.ID_NOTA=y.KD_UPER left join TB_USER tu on z.USER_ID=TU.ID where z.NO_CONTAINER='$ct'        order by z.TGL_SIMPAN DESC";			$eq=$db->query($sq);	$rq=$eq->getAll();?><style>.std{		font:italic bold 12px Helvetica;	color:#6d6b6b;	text-align:center;	background-color:#ffffff;	height:30px;}.std2{		font:italic bold 12px Helvetica;	color:#f91550;	text-align:center;	background-color:#ffffff;	height:30px;}</style><table>	<tr>		<th class="grid-header" width="20">No</th>   		<th class="grid-header" width="200">No. Request</th>		<th class="grid-header" width="100">No. Invoice</th>		<th class="grid-header" width="200">Tax Number</th>		<th class="grid-header" width="100">Status</th>		<th class="grid-header" width="100">Date Request</th>		<th class="grid-header" width="100">Date Payment</th>		<th class="grid-header" width="100">Paid Thru</th>		<th class="grid-header" width="100">User</th>    </tr>	<?php	$no=1;		foreach ($rq as $row) {	?>	<tr>		<td class="std"><?=$no;?></td>		<td  class="std"><?=$row['ID_REQ'];?></td>		<td  class="std"><?=$row['ID_NOTA'];?></td>		<td  class="std"><?=$row['NO_FAKTUR'];?></td>		<td  class="std"><?=$row['STATUS'];?></td>		<td  class="std"><?=$row['TGL_SIMPAN'];?></td>		<td  class="std"><?=$row['TGL_PAYMENT'];?></td>		<td  class="std"><?=$row['TGL_BERLAKU'];?></td>		<td  class="std"><?=$row['NAME'];?></td>	</tr>	<?php $no++; }?></table>