<?php$no_cont = $_POST["NO_CONT"];$no_booking = $_POST["NO_BOOKING"];$counter = $_POST["COUNTER"];$db = getDB("storage");$query = $db->query("SELECT P.STATUS_CONT, P.TGL_UPDATE FROM HISTORY_CONTAINER PWHERE P.NO_CONTAINER = '$no_cont' AND P.NO_BOOKING = '$no_booking'ORDER BY P.TGL_UPDATE DESC");$result = $query->fetchRow();$status = $result["STATUS_CONT"];echo $status;exit();?>