<?php	$voyin=$_GET['voyin'];	$voyout=$_GET['voyout'];	$ct=$_GET['id_ct'];	$db=getDb();	  $sq="select distinct NO_CONTAINER, BH_NOTA.ID_NOTA, NO_FAKTUR, BH_NOTA.ID_REQUEST AS ID_REQ, EMKL, STATUS, ALAMAT_EMKL AS ALAMAT, VESSEL , ' ' AS VOYAGE_IN, VOYAGE AS VOYAGE_OUT, TGL_CETAK AS TGL_SIMPAN,TGL_PAYMENT, PAYMENT_VIA, TOTAL, COA, '' AS KD_MODUL, 'BH' KET from BH_NOTA INNER JOIN BH_DETAIL_NOTA ON (trim(BH_NOTA.ID_NOTA)=trim(BH_DETAIL_NOTA.ID_NOTA))      where NO_CONTAINER='$ct'        order by TGL_SIMPAN DESC";			$eq=$db->query($sq);	$rq=$eq->getAll();?><style>.std{		font:italic bold 12px Helvetica;	color:#6d6b6b;	text-align:center;	background-color:#ffffff;	height:30px;}.std2{		font:italic bold 12px Helvetica;	color:#f91550;	text-align:center;	background-color:#ffffff;	height:30px;}</style><table>	<tr>		<th class="grid-header" width="20">No</th>   		<th class="grid-header" width="200">No. Request</th>		<th class="grid-header" width="100">No. Invoice</th>		<th class="grid-header" width="200">Tax Number</th>		<th class="grid-header" width="100">Status</th>		<th class="grid-header" width="100">Date Request</th>		<th class="grid-header" width="100">Date Payment</th>		<th class="grid-header" width="100">User</th>    </tr>	<?php	$no=1;		foreach ($rq as $row) {	?>	<tr>		<td class="std"><?=$no;?></td>		<td  class="std"><?=$row['ID_REQ'];?></td>		<td  class="std"><?=$row['ID_NOTA'];?></td>		<td  class="std"><?=$row['NO_FAKTUR'];?></td>		<td  class="std"><?=$row['STATUS'];?></td>		<td  class="std"><?=$row['TGL_SIMPAN'];?></td>		<td  class="std"><?=$row['TGL_PAYMENT'];?></td>		<td  class="std"><?=$row['ID_USER'];?></td>	</tr>	<?php $no++; }?></table>