<?	$no_ukk  = $_GET['ukk'];	$no_cont  = $_GET['cont'];		$db = getDB();	$info = "SELECT A.NM_KAPAL||' '||A.VOYAGE_IN||'/'||A.VOYAGE_OUT AS VESVOY, 	                B.NO_CONTAINER,				    B.SIZE_||'/'||B.TYPE_||'/'||B.STATUS AS SPEC_CONT,					CASE WHEN B.HZ IS NULL THEN 'T'					     ELSE B.HZ END HZ,					B.IMO,					B.UN_NUMBER,					B.HANDLING_INSTRUCTION AS HI,					B.CARRIER,					TRIM(B.TYPE_) AS TIPE,					TRIM(B.TEMP) AS TEMP,					B.OVER_HEIGHT,					B.OVER_LENGTH,					B.OVER_WIDTH,					CASE WHEN TRIM(B.HEIGHT) IS NULL THEN 'NOT'					     ELSE TRIM(B.HEIGHT) END OOG_RMR				FROM RBM_H A, ISWS_LIST_CONTAINER B 		     WHERE B.NO_UKK = '$no_ukk'			    AND B.NO_CONTAINER = '$no_cont'				AND TRIM(A.NO_UKK) = TRIM(B.NO_UKK)";    $detail = $db->query($info);	$hsl_cont = $detail->fetchRow();	$vesvoy = $hsl_cont['VESVOY'];	$nocont = $hsl_cont['NO_CONTAINER'];	$spec = $hsl_cont['SPEC_CONT'];	$hz = $hsl_cont['HZ'];	$imo = $hsl_cont['IMO'];	$un = $hsl_cont['UN_NUMBER'];	$cr = $hsl_cont['CARRIER'];	$og = $hsl_cont['OOG_RMR'];	$ovh = $hsl_cont['OVER_HEIGHT'];	$ovl = $hsl_cont['OVER_LENGTH'];	$ovw = $hsl_cont['OVER_WIDTH'];	$hi = $hsl_cont['HI']; 	$tp_cont = $hsl_cont['TIPE'];	$tmp = $hsl_cont['TEMP']; ?><br>		<table width="300">			<tr height='20'>				<td class="form-field-caption" align="right"></td>				<td class="form-field-caption" align="right"></td>				<td></td>						</tr>			<tr>				<td class="form-field-caption" align="right">Vessel/Voyage</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="vesvoy" id="vesvoy" size="25" value="<?=$vesvoy?>" readonly="readonly"/>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">Container ID</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="cont_no" id="cont_no" size="12" value="<?=$nocont?>" readonly="readonly"/>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">SZ/TY/ST</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="spec_cont" id="spec_cont" size="10" value="<?=$spec?>" readonly="readonly"/>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">Carrier</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="carrier" id="carrier" size="5" value="<?=$cr?>"/>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">HZ</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="hz_cont" id="hz_cont" size="2" value="<?=$hz?>" readonly="readonly"/>				</td>			</tr>			<?				if($hz=='Y')				{			?>			<tr>				<td class="form-field-caption" align="right">IMO Class</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="dg_class" id="dg_class" size="5" value="<?=$imo?>"/>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">UN Number</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="un_number" id="un_number" size="6" value="<?=$un?>"/>				</td>			</tr>						<? } ?>			<? if($tp_cont=='RFR') { ?>			<tr>				<td class="form-field-caption" align="right">Set Point</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<input type="text" name="tmp_rfr" id="tmp_rfr" size="4" value="<?=$tmp?>"/> :CEL				</td>			</tr>			<? } ?>			<? 				if($og=='OOG')				{			?>			<tr>				<td class="form-field-caption" align="right">Height</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<select name="oog_stat" id="oog_stat">						<option value="">Standard</option>						<option value="OOG" selected="yes">OOG</option>					</select>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">Dimension</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					OH <input type="text" name="oh" id="oh" size="5" value="<?=$ovh?>"/> CM<br/>					OL <input type="text" name="ol" id="o" size="5" value="<?=$ovl?>"/> CM<br/>					OW <input type="text" name="ow" id="o" size="5" value="<?=$ovw?>"/> CM				</td>			</tr>			<? }				else				{ ?>			<tr>				<td class="form-field-caption" align="right">Height</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<select name="oog_stat" id="oog_stat">						<option value="" selected="yes">Standard</option>						<option value="OOG">OOG</option>					</select>				</td>			</tr>			<tr>				<td class="form-field-caption" align="right">Dimension</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					OH <input type="text" name="oh" id="oh" size="5" value="<?=$ovh?>"/> CM<br/>					OL <input type="text" name="ol" id="o" size="5" value="<?=$ovl?>"/> CM<br/>					OW <input type="text" name="ow" id="o" size="5" value="<?=$ovw?>"/> CM				</td>			</tr>			<? } ?>						<tr>				<td class="form-field-caption" align="right">Handling</td>				<td class="form-field-caption" align="right"> : </td>				<td class="form-field-caption" align="left">					<select name="hi_stat" id="hi_stat">						<option value="">--choose--</option>						<?php 								$db = getDB();								$query_get_block     = "SELECT KD_HI, KETERANGAN 														FROM MASTER_HANDLING_INSTRUCTION";								$result_block	= $db->query($query_get_block);								$row_block		= $result_block->getAll();								foreach($row_block as $row)								{						?>						<option value="<?=$row['KD_HI']?>"><?=$row['KETERANGAN']?></option>						<?  } ?>					</select>				</td>			</tr>			<tr>				<td colspan="3">&nbsp;</td>			</tr>			<tr>				<td colspan="3" align="right">								<input type="button" name="update container" value="&nbsp;Update&nbsp;" onclick="update_cont('<?=$no_ukk?>','<?=$no_cont?>')"/>				</td>			</tr>			<tr height='20'>				<td class="form-field-caption" align="right"></td>				<td class="form-field-caption" align="right"></td>				<td></td>						</tr>		</table>