<?php	$vessel	= $_POST['vessel'];	$voyin	= $_POST['voyin'];	$voyout = $_POST['voyout'];	$kd_pel = $_POST['kd_pelanggan'];	$user   = $_SESSION['PENGGUNA_ID'];	$d_i 	= $_POST['d_i']; 		$db=getDb();		//echo "begin REQ_STACKEXT_OPUS('$vessel','$voyin','$voyout','$user','$kd_pel');end;";die;		$query_cek  = "SELECT COUNT(1) JML FROM REQ_STACKEXT_H WHERE VESSEL = '$vessel' AND VOYAGE_IN = '$voyin' AND VOYAGE_OUT = '$voyout' and status <> 'X'";	$result_cek = $db->query($query_cek);	$hasil_cek  = $result_cek->fetchRow();		$jml 		= $hasil_cek['JML'];		if($jml > 0){		echo "EXIST";	} else {	//echo "begin REQ_STACKEXT_OPUS('$vessel','$voyin','$voyout','$user','$kd_pel');end;";		$query="begin REQ_STACKEXT_OPUS('$vessel','$voyin','$voyout','$user','$kd_pel', '$d_i');end;";		$db->query($query);				echo 'OK';	}?>