<?php	$id_ukk=$_POST['UKK'];	$rta=$_POST['RTA'];	$user=$_SESSION['PENGGUNA_ID'];	$param_b_var= array(							"v_ukk"=>"$id_ukk",						"v_user"=>"$user",						"v_rta"=>"$rta",						"v_msg"=>""						);	$db=getDb();		$query="begin REQ_REEKSPOR(:v_ukk,:v_user,to_date(:v_rta,'dd-mm-yyyy hh24:mi'),:v_msg);end;";	$db->query($query,$param_b_var);	//print_r($query);die;	$msg = $param_b_var['v_msg'];			echo $msg;?>