<?php$db = getDB("storage");$tgl_open = $_POST['tgl_open'];$tgl_close = $_POST['tgl_close'];$close_time = $_POST['close_time'];$no_booking = $_POST['no_booking'];$qdual 		= "select TO_DATE('$tgl_open','DD/MM/YYYY') TGL_OPEN, TO_DATE('$tgl_close','DD/MM/YYYY') TGL_CLOSE, TO_DATE('$close_time','DD/MM/YYYY') CLOSE_TIME FROM DUAL";$rdual 		= $db->query($qdual);$rw 		= $rdual->fetchRow();$tgl_open 	= $rw[TGL_OPEN];$tgl_close 	= $rw[TGL_CLOSE];$close_time 	= $rw[CLOSE_TIME];if( strtotime($tgl_close) > strtotime($close_time)){	echo 'V';	exit();}else if( strtotime($tgl_open) > strtotime($close_time)){	echo 'X';	exit();} else {	$q_save = "insert into master_booking_time (open, close, no_booking, aktif) values ('$tgl_open','$tgl_close','$no_booking','Y')";	$db->query($q_save);	echo 'Y';	exit();}?>